/* Procedure structure for procedure `ticket_buy` */

/*!50003 DROP PROCEDURE IF EXISTS  `ticket_buy` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `ticket_buy`(ticketId LONG, schedId INT)
BEGIN
	UPDATE ticket SET ticket_status=9 WHERE ticket_id=ticketId AND sched_id=schedId;
	UPDATE ticket SET ticket_locked_time='1900-01-01 00:00:00' WHERE ticket_id=ticketId AND sched_id=schedId;
    END */$$
