DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `ticket_lock`(ticketId LONG,schedId LONG,locked_time DATETIME)
BEGIN
	UPDATE ticket SET ticket_status=1 WHERE ticket_id=ticketId AND sched_id=schedId;
	UPDATE ticket SET ticket_locked_time=locked_time WHERE ticket_id=ticketId AND sched_id=schedId;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `ticket_refund` */

